import { Entity, SystemBuilder, XY, isometricToWorld } from "@piggo-gg/core";
import { Container } from "pixi.js";


const precalc = () => {
}

const tileIndex = (num: number): number => {
  let numZeros = 0;
  for (let i = 0; i < num; i++) {
    if (tileMap[i] === 0) numZeros++;
  }
  return num - numZeros;
}

const castRay = (start: XY, direction: XY, maxDistance: number, floorTilesArray: Entity) => {
  const visibleTiles = [];

  for (let i = 0; i < maxDistance; i++) {
    const x = Math.round(start.x + direction.x * i);
    const y = Math.round(start.y + direction.y * i);

    if (tileMap[x + (y * 80)] === 0) break;

    const index = tileIndex(x + (y * 80));
    const tile = floorTilesArray.components.renderable?.c.children[index];
    if (!tile) continue;
    visibleTiles.push(tile);
  }
  return visibleTiles;
}

export const SightSystem: SystemBuilder<"SightSystem"> = {
  id: "SightSystem",
  init: ({ world }) => {

    let last: { tint: number, container: Container }[] = [];

    return {
      id: "SightSystem",
      query: [],
      onTick: () => {
        if (!world.client) return;

        const floorTilesArray = world.entities["floorTilesArray"];
        if (!floorTilesArray) return;

        const { playerEntity } = world.client;
        const skelly = world.entities[playerEntity.components.controlling.data.entityId];
        if (!skelly) return;

        const { position } = skelly.components;
        if (!position) return;

        const { x, y } = isometricToWorld(position.data)

        const tileX = Math.round(x / 32) - 1;
        const tileY = Math.round(y / 32);

        const index = tileIndex(tileY * 80 + tileX);
        const child = floorTilesArray.components.renderable?.c.children[index];
        if (!child) return;

        const visibleTiles: (Container | undefined)[] = [child];

        last.forEach(({ container, tint }) => {
          // container.tint = tint;
          container.alpha = 0.5;
        });


        const distanceDefault = 11;
        const directions: (XY & { plus?: number })[] = [
          { x: 1, y: 0 },
          { x: 0, y: 1 },
          { x: -1, y: 0 },
          { x: 0, y: -1 },
          { x: 0.1, y: 0.9 },
          { x: 0.2, y: 0.8 },
          { x: 0.3, y: 0.7, plus: 1 },
          { x: 0.4, y: 0.6, plus: 1 },
          { x: 0.45, y: 0.55, plus: 1 },
          { x: 0.5, y: 0.5, plus: 2 },
          { x: 0.55, y: 0.45, plus: 1 },
          { x: 0.6, y: 0.4, plus: 1 },
          { x: 0.7, y: 0.3, plus: 1 },
          { x: 0.8, y: 0.2 },
          { x: 0.9, y: 0.1 },

          { x: -0.1, y: 0.9 },
          { x: -0.2, y: 0.8 },
          { x: -0.3, y: 0.7, plus: 1 },
          { x: -0.4, y: 0.6, plus: 1 },
          { x: -0.45, y: 0.55, plus: 1 },
          { x: -0.5, y: 0.5, plus: 2 },
          { x: -0.55, y: 0.45, plus: 1 },
          { x: -0.6, y: 0.4, plus: 1 },
          { x: -0.7, y: 0.3, plus: 1 },
          { x: -0.8, y: 0.2 },
          { x: -0.9, y: 0.1 },

          { x: 0.1, y: -0.9 },
          { x: 0.2, y: -0.8 },
          { x: 0.3, y: -0.7, plus: 1 },
          { x: 0.4, y: -0.6, plus: 1 },
          { x: 0.45, y: -0.55, plus: 1 },
          { x: 0.5, y: -0.5, plus: 2 },
          { x: 0.55, y: -0.45, plus: 1 },
          { x: 0.6, y: -0.4, plus: 1 },
          { x: 0.7, y: -0.3, plus: 1 },
          { x: 0.8, y: -0.2 },
          { x: 0.9, y: -0.1 },

          { x: -0.1, y: -0.9 },
          { x: -0.2, y: -0.8 },
          { x: -0.3, y: -0.7, plus: 1 },
          { x: -0.4, y: -0.6, plus: 1 },
          { x: -0.45, y: -0.55, plus: 1 },
          { x: -0.5, y: -0.5, plus: 2 },
          { x: -0.55, y: -0.45, plus: 1 },
          { x: -0.6, y: -0.4, plus: 1 },
          { x: -0.7, y: -0.3, plus: 1 },
          { x: -0.8, y: -0.2 },
          { x: -0.9, y: -0.1 }
        ]

        directions.forEach(({ x, y, plus = 0 }) => {
          const rayCast = castRay({ x: tileX, y: tileY }, { x, y }, distanceDefault + plus, floorTilesArray);
          visibleTiles.push(...rayCast);
        });

        visibleTiles.forEach((tile) => {
          if (!tile) return;
          last.push({ container: tile, tint: tile.tint });
        });

        visibleTiles.forEach((tile) => {
          // if (tile) tile.tint = 0x00aaff;
          if (tile) tile.alpha = 1;
          // if (tile) tile.filters = [];
        });

        // child.tint = 0x00ff00;
      }
    }
  }
}

